import { useLoaderData } from 'react-router-dom'
// import image from '../assets/images/Gourav.png'
const NameAndImage = () => {
    const user = useLoaderData()
    return (
        <div class="hero">

            <div class="container">

                <div class="left">

                    <h1 class="h1">
                        Hi, I'm <b>{user.firstName}&nbsp;{user.lastName}</b>.
                        {user.role && <span>{user.role}</span>}
                    </h1>

                    {user.specialization && <p class="h3">
                        {user.specialization}
                    </p>}

                    <div class="btn-group">
                        <a href="/" class="btn btn-primary">Contact Me</a>
                        <a href="/profile" class="btn btn-secondary">About Me</a>
                    </div>

                </div>

                <div class="right">

                    <div class="pattern-bg"></div>
                    {user.avatar && <div class="img-box">
                        <img src={'http://localhost:3080/users/avatar/' + user._id} alt="Julia Walker" class="hero-img" />
                        <div class="shape shape-1"></div>
                        <div class="shape shape-2"></div>
                    </div>}

                </div>

            </div>

        </div>
    )
}

export default NameAndImage
