import { useState } from "react"
import { Form, useParams, useSubmit } from "react-router-dom";

const BlogForm = ({ blog }) => {
    const params = useParams()
    const submit = useSubmit()
    const [title, setTitle] = useState('');
    const [image, setImage] = useState('');
    const [description, setDescription] = useState('')
    const [link, setLink] = useState('')

    const postBlogHandler = (e) => {
        e.preventDefault();
        const method = blog ? 'PATCH' : 'POST'
        const action = blog ? '/blogs/new' : `blogs/${params.BlogId}/edit`
        const newBlog = blog ? blog : { title: title, picture: image, description: description, link: link };
        submit(newBlog, { method: method, action: action })
    }
    return (
        <div class="container">
            <Form class="blog-form" action="#" method="post" enctype="multipart/form-data">
                <div class="form-group">
                    <label for="title">Title</label>
                    <input type="text" class="form-control" id="title" name="title" onChange={(e) => setTitle(e.target.value)} defaultValue={blog ? blog.title : ""} />
                </div>
                <div class="form-group">
                    <label for="image">Image</label>
                    <input type="text" class="form-control" id="image" name="picture" onChange={(e) => setImage(e.target.value)} defaultValue={blog ? blog.picture : ""} />
                </div>
                <div class="form-group">
                    <label for="description">Description</label>
                    <textarea class="form-control" id="description" name="description" rows="1000" onChange={(e) => setDescription(e.target.value)} defaultValue={blog ? blog.description : ""}></textarea>
                </div>
                <div class="form-group">
                    <label for="link">Link</label>
                    <input class="form-control" id="link" name="link" onChange={(e) => setLink(e.target.value)} defaultValue={blog ? blog.link : ""} />
                </div>
                <button type="submit" class="btn btn-primary" onSubmit={postBlogHandler}>Submit</button>
            </Form>
        </div>
    )
}

export default BlogForm
